```js
function* main() {
  try {
    const users = yield ajax(url1);
    console.log(users);

    const posts = yield ajax(url2);
    console.log(posts);
  } catch (e) {
    console.log(e);
  }
}
function co(generator) {
  const g = generator();

  function handleResult(result) {
    if (result.done) return;
    result.value.then(
      (data) => {
        handleResult(g.next(data));
      },
      (error) => {
        g.throw(error);
      }
    );
  }

  handleResult(g.next());
}

co(main);
```

